  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  name!: string;
  password!: string;

  ngOnInit() {
  }

  login() {
    let div = document.getElementById('emailInput');
    console.log(div, this.name, this.password);
  }
}
